function initAutoComplete(a,b,c){p_width=jQuery(""+a).width();if(p_width==0)p_width=200;jQuery(""+a).autocomplete(b,{width:p_width,multiple:true,matchContains:true,selectFirst:false,formatItem:formatItem,formatResult:formatResult,minChars:c,multipleSeparator:", "})}function formatResult(a){return a[1].replace(/(<.+?>)/gi,"")}function formatItem(a){return a[1]}