function addTerm(a,b){var c=document.getElementById(b);if(c.value.length>0&&!c.value.match(/,\s*$/))c.value+=", ";var d=new RegExp(a+",");if(!c.value.match(d))c.value+=a+", ";return true}jQuery(document).ready(function(){jQuery("#term-list-inner a").click(function(a){a.preventDefault();addTerm(this.innerHTML,"renameterm_old");addTerm(this.innerHTML,"deleteterm_name");addTerm(this.innerHTML,"addterm_match");return false})})